import { useEffect, useState } from "react";
import "./App.css";

import AddItems from "./Components/AddItems/AddItems";
import ListItem from "./Components/Items/ListItem";
import {
  collection,
  query,
  onSnapshot,
  doc,
  updateDoc,
  deleteDoc,
} from "firebase/firestore";
import { db } from "./firebase";

const Dummy_List = [];

const App = () => {
  // const [enteredItem, setEnteredItem] = useState(Dummy_List);
  const [todos, setTodos] = useState([]);
  useEffect(() => {
    const q = query(collection(db, "todos"));
    const unsub = onSnapshot(q, (querySnapshot) => {
      let todosArray = [];
      querySnapshot.forEach((doc) => {
        todosArray.push({ ...doc.data(), id: doc.id });
      });
      setTodos(todosArray);
    });
    return () => unsub();
  }, []);

  const addItemHandler = (title) => {
    setTodos((prevItems) => {
      return [...prevItems, title];
    });
  };

  const deleteItemHandler = (itemId) => {
    setTodos((prevItems) => {
      const updatedGoals = prevItems.filter((item) => item.id !== itemId);
      return updatedGoals;
    });
  };

  return (
    <div className="App">
      <section className="container-sm  col-6 mt-5">
        <AddItems onAddItems={addItemHandler} />
        <ListItem items={todos} onDeleteItem={deleteItemHandler} />
      </section>
     
    </div>
  );
};

export default App;
