import React, { useState } from "react";

export default function Todo({
  todo,
  toggleComplete,
  handleDelete,
  handleEdit,
}) {
  const [newEnteredTitle, setnewEnteredTitle] = useState(todo.title);

  const handleChange = (event) => {
    event.preventDefault();
    if (todo.complete === true) {
      setnewEnteredTitle(todo.title);
    } else {
      todo.title = "";
      setnewEnteredTitle(event.target.value);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={todo.title === "" ? newEnteredTitle : todo.title}
        onChange={handleChange}
      />
      <button
        onClick={() => {
          toggleComplete(todo);
        }}
      >
        Add
      </button>
      <button
        className="button-edit"
        onClick={() => handleEdit(todo, newEnteredTitle)}
      >
      </button>
      <button onClick={() => handleDelete(todo.id)}>delete</button>{" "}
    </div>
  );
}
